mapper.select:
SELECT t_r.register_id AS registerId, t_r.register_no AS registerNo, t_r.activity_id AS activityId, t_r.user_id AS userId, t_r.nick_name AS nickName, t_r.memo, t_r.portrait, DATE_FORMAT(t_r.create_time,'%Y-%m-%d %H:%i:%s') AS createTime, t_r.content, SUM(CASE t_v.register_id IS NULL WHEN 1 THEN 0 ELSE 1 END) AS voteCount, SUM(CASE t_v.voter_id WHEN #{userId,jdbcType=VARCHAR} THEN 1 ELSE 0 END)>0 AS voteStatus FROM act_activity_register t_r LEFT JOIN act_activity_vote t_v ON t_r.register_id = t_v.register_id AND t_v.del_flag = '0' WHERE t_r.del_flag = '0' AND t_r.activity_id = #{activityId,jdbcType=VARCHAR}
AND(t_r.nick_name like concat('%',#{search,jdbcType=VARCHAR},'%') OR t_r.register_no like concat('%',#{search,jdbcType=VARCHAR},'%'))
GROUP BY t_r.register_id ORDER BY voteCount DESC, t_r.register_no ASC
