mapper.select:
SELECT
FROM commodity WHERE del_flag = '0'
AND (commodity_name LIKE BINARY CONCAT('%',#{search},'%') OR commodity_type IN (SELECT type_id FROM commodity_type WHERE del_flag = '0' AND type_name LIKE BINARY CONCAT('%',#{search},'%')) OR commodity_brand IN (SELECT brand_id FROM commodity_brand WHERE del_flag = '0' AND (brand_name_chi LIKE BINARY CONCAT('%',#{search},'%') OR brand_name_eng LIKE BINARY CONCAT('%',#{search},'%') OR brand_name_tag LIKE BINARY CONCAT('%',#{search},'%'))))
#{type}
#{brand}
LIMIT ${iDisplayStart}, ${iDisplayLength}
