mapper.select:
SELECT t_c.commodity_id AS commodityId, t_c.commodity_name AS commodityName, t_c.commodity_type AS commodityType, t_t.type_name AS commodityTypeValue, t_c.commodity_brand AS commodityBrand, t_b.brand_name_chi AS brandNameChi, t_b.brand_name_eng AS brandNameEng, t_c.price, t_c.inventory, t_c.on_sale AS onSale, CASE t_c.on_sale WHEN '0' THEN '在售' WHEN '1' THEN '停售' ELSE '' END AS onSaleValue, t_c.on_conversion AS onConversion, CASE t_c.on_conversion WHEN '0' THEN '不可兑换' WHEN '1' THEN '可兑换' ELSE '' END AS onConversionValue, IFNULL(DATE_FORMAT(t_c.sale_start_datetime,'%Y-%m-%d %H:%i:%s'),'不限') AS saleStart, IFNULL(DATE_FORMAT(t_c.sale_end_datetime,'%Y-%m-%d %H:%i:%s'),'不限') AS saleEnd FROM commodity t_c LEFT JOIN commodity_brand t_b ON t_c.commodity_brand = t_b.brand_id LEFT JOIN commodity_type t_t ON t_c.commodity_type = t_t.type_id WHERE t_c.del_flag = '0'
AND (t_c.commodity_name LIKE BINARY CONCAT('%',#{search},'%') OR t_b.brand_name_chi LIKE BINARY CONCAT('%',#{search},'%') OR t_b.brand_name_eng LIKE BINARY CONCAT('%',#{search},'%') OR t_t.type_name LIKE BINARY CONCAT('%',#{search},'%') )
#{type}
#{brand}
ORDER BY CONVERT(t_c.commodity_name USING gbk) ASC

SELECT t_c.commodity_id AS commodityId, t_c.commodity_name AS commodityName, t_c.commodity_type AS commodityType, t_t.type_name AS commodityTypeValue, t_c.commodity_brand AS commodityBrand, t_b.brand_name_chi AS brandNameChi, t_b.brand_name_eng AS brandNameEng, t_c.carousel_pic1 AS commodityPic, t_b.logo_url AS brandLogo, t_c.specification, t_c.price, t_c.sale_price AS salePrice, t_c.conversion_coin AS conversionCoin, t_c.conversion_price AS conversionPrice, t_c.inventory, t_c.on_sale AS onSale, CASE t_c.on_sale WHEN '0' THEN '在售' WHEN '1' THEN '停售' ELSE '' END AS onSaleValue, t_c.on_conversion AS onConversion, CASE t_c.on_conversion WHEN '0' THEN '不可兑换' WHEN '1' THEN '可兑换' ELSE '' END AS onConversionValue, IFNULL(DATE_FORMAT(t_c.sale_start_datetime,'%Y-%m-%d %H:%i:%s'),'不限') AS saleStart, IFNULL(DATE_FORMAT(t_c.sale_end_datetime,'%Y-%m-%d %H:%i:%s'),'不限') AS saleEnd, IFNULL(t_sale.saleCount,0) AS saleCount FROM commodity t_c LEFT JOIN commodity_brand t_b ON t_c.commodity_brand = t_b.brand_id LEFT JOIN commodity_type t_t ON t_c.commodity_type = t_t.type_id LEFT JOIN (SELECT commodity_id, COUNT(1) AS saleCount FROM order_pay WHERE order_status = '2' AND commodity_type = '0' AND del_flag = '0' GROUP BY commodity_id ) t_sale ON t_c.commodity_id = t_sale.commodity_id WHERE t_c.del_flag = '0'
AND (t_c.commodity_name LIKE BINARY CONCAT('%',#{search},'%') OR t_b.brand_name_chi LIKE BINARY CONCAT('%',#{search},'%') OR t_b.brand_name_eng LIKE BINARY CONCAT('%',#{search},'%') OR t_t.type_name LIKE BINARY CONCAT('%',#{search},'%') )
#{type}
#{brand}
ORDER BY CONVERT(t_c.commodity_name USING gbk) ASC
